
public class Ejercicio3App {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x, y;
		double n, m;

		x = 10;
		y = 15;
		n = 0.95;
		m = 1.3;

		int suma = x + y;
		int dif = x - y;
		int prod = x * y;
		int div = x / y;
		int resto = x % y;
		double sumaD = n + m;
		double difD = n - m;
		double prodD = n * m;
		double divD = n / m;
		double restoD = n % m;
		double sumaM = x + n;
		double divM = y / m;
		double restoM = y % m;
		double multAll = x * y * m * n;

		System.out.println(x);
		System.out.println(y);
		System.out.println(n);
		System.out.println(m);
		System.out.println(suma);
		System.out.println(dif);
		System.out.println(prod);
		System.out.println(div);
		System.out.println(resto);
		System.out.println(sumaD);
		System.out.println(difD);
		System.out.println(prodD);
		System.out.println(divD);
		System.out.println(restoD);
		System.out.println(sumaM);
		System.out.println(divM);
		System.out.println(restoM);
		System.out.println(2 * x);
		System.out.println(2 * y);
		System.out.println(2 * n);
		System.out.println(2 * n);
		System.out.println(multAll);

	}

}
