
public class unitarios {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
	int operador=10;
	System.out.println(operador);
	System.out.println(operador++); //Aquí muestra como resultado el valor del operador y luego le suma 1. Ahora vale 11.
	System.out.println(++operador); //Aquí suma 1 al valor del operador anterior (11) y luego muestra el resultado.
	}

}
